DROP TABLE IF EXISTS `ycm_shop_order_log`;
CREATE TABLE `ycm_shop_order_log` (
  `action_id` int(10) unsigned NOT NULL AUTO_INCREMENT COMMENT '自增id',
  `order_id` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '订单ID',
